window.onload=function(){var e=document.querySelector(".file-input"),t=document.querySelector(".preview"),n=document.querySelector(".modal-content .file-input"),r=document.querySelector(".modal-content .preview");e.addEventListener("change",function(){document.querySelector(".resume-container__label").classList.add("active");for(;t.firstChild;)t.removeChild(t.firstChild);var n=e.files;if(n[0].size>5e6){var r=document.createElement("p");r.textContent="Превышен лимит МБ",t.appendChild(r)}else{var r=document.createElement("p");r.textContent="Загружено: "+n[0].name,t.appendChild(r)}}),n.addEventListener("change",function(){document.querySelector(".resume-container__label").classList.add("active");for(;r.firstChild;)r.removeChild(r.firstChild);var e=n.files;if(e[0].size>5e6){var t=document.createElement("p");t.textContent="Превышен лимит МБ",r.appendChild(t)}else{var t=document.createElement("p");t.textContent="Загружено: "+e[0].name,r.appendChild(t)}})};